            </div>

            <div class="task-submit">
                <a class="btn btn-primary" href="{% url 'submit' task.id %}">Submit Task</a>
            </div>

            <div class="task-actions">
                <a class="btn btn-ghost" href="{% url 'student_dashboard' %}">Back to dashboard</a>
            </div>
        </aside>
    </div>

    {% if user_role == "teacher" %}
        <div class="task-student-list">
            <h2>Connected students:</h2>
            <ul id="student-list"></ul>
        </div>
    {% endif %}

    {{ autosave_url|json_script:"autosave-url-data" }}
    {{ csrf_token|json_script:"csrf-token-data" }}
    {{ user_role|json_script:"user-role" }}
    {{ user_id|json_script:"user-id" }}
    <script>
        window.isLive = {{ is_live|yesno:"true,false" }}
        window.docID = {{ document.id }}
        window.WS_URL = `ws://${window.location.host}/ws/editor/room/{{ task.id }}/`;
        const userRole = JSON.parse(document.getElementById('user-role').textContent)
        const userId = JSON.parse(document.getElementById('user-id').textContent)
        window.CURRENT_STUDENT_ID = null
        if(userRole === "student"){
            window.CURRENT_STUDENT_ID = userId
        }
        window.STUDENT_CACHE = {}
    </script>
    {% vite_asset 'js/editor.js' %}
</div>
{% endblock %}

